(function ($) {
  const emailRegex =
    /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;

  const formSubmitFn = function (e) {
    if (e) {
      e.preventDefault();
      e.stopPropagation();
    }

    const $form = $(this).hasClass("newsletter-signup-field")
      ? $(this)
      : $(this).parents(".newsletter-signup-field");
    const $input = $form.find("input[name=email]");

    // Don't let them submit again if they've succeeded once
    if ($form.hasClass("no-submit")) {
      return;
    }

    // Not a valid email
    if (!emailRegex.test($input.val())) {
      $form.addClass("invalid");
      return;
    }

    // If you've got this far, we've validated your email.
    $form.removeClass("invalid");

    $form.addClass("no-submit");
    $form.find(".spinner").removeClass("d-none");
    $form.find(".button-text").addClass("d-none");

    // Make the AJAX call to Klaviyo
    $.post(
      $form.data("action") || $form.attr("action"),
      $form.find(":input").serialize(),
      function () {
        // Clear input and dismiss modal on success
        $form.find("input[type=email]").val("");

        $form.find(".spinner").addClass("d-none");
        $form.find(".button-text").removeClass("d-none");

        const $marketingPopup = $form.parents("#marketing-popup");

        if ($marketingPopup && $marketingPopup.length > 0) {
          $marketingPopup.modal("hide");
        }

        if ($form.parent().attr("id") === "item-added-alert") {
          $form.parent().slideUp(400, function () {
            $(this).remove();
          });
        }
      }
    ).fail(function () {
      // Let people re-submit on failure
      $form.removeClass("no-submit");
      $form.find(".spinner").addClass("d-none");
      $form.find(".button-text").removeClass("d-none");
    });

    return;
  };

  $(document).on("submit", "form.newsletter-signup-field", formSubmitFn);
  $(document).on(
    "click",
    "div.newsletter-signup-field button[type=button]",
    formSubmitFn
  );
  $(document).on(
    "keypress",
    "div.newsletter-signup-field input[type=email]",
    function (e) {
      if (e.which === 13) {
        formSubmitFn.call(this, e);
      }
    }
  );
})(jQuery);

