document.addEventListener("DOMContentLoaded", function () {
  const container = document.getElementById(`BlogTags`);

  if (container) {
    const updateUrl = event => {
      const value = event.target.value;
      const href = window.location.href;
      const pathname = window.location.pathname;

      console.log(value, pathname, window.location);

      if (value) {
        if (pathname.includes(`tagged`)) {
          // replace current tag in url
          const newHref = href.substring(0, href.lastIndexOf(`/`));
          window.location.href = `${newHref}/${value}`;
        } else {
          if (href.endsWith(`/`)) {
            window.location.href = `${href}tagged/${value}`;
          } else {
            window.location.href = `${href}/tagged/${value}`;
          }
        }
      } else {
        if (pathname.includes(`tagged`)) {
          window.location.href = href.substring(0, href.lastIndexOf(`tagged`));
        }
      }
    };

    container.addEventListener(`change`, updateUrl, false);
  }
});

function init() {
  const setVh = function () {
    // get the viewport height and we multiple it by 1% to get a value for a vh unit
    let vh = window.innerHeight * 0.01;
    // set the value in the --vh custom property to the root of the document
    document.documentElement.style.setProperty("--vh", `${vh}px`);
  };

  setVh();
  window.addEventListener("resize", setVh, false);
}

window.onload = init;

document.addEventListener(`DOMContentLoaded`, function () {
  /**
   * Track elements as they enter the viewport
   *
   * @since   1.0
   */
  const viewportTracking = function () {
    if (document.querySelector(`.ev`)) {
      const track = selector => {
        enterView({
          selector: selector,
          enter: function (e) {
            e.classList.add("ev--entered");
          },
          offset: "25",
          once: true,
        });
      };

      track(`.ev`);
    }
  };

  /**
   * Chocks away!
   */
  viewportTracking();
});

